INSERT INTO `precautions_tbl` (`id`, `title`, `details`, `picture`, `post_by`, `post_date`, `post_category`) VALUES
(1, 'Wear a mask', '<div class=\"PZPZlf\">\r\n<div class=\"INGOOb\" data-hveid=\"CAUQAg\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAEwAnoECAUQAg\">To prevent the spread of COVID-19:</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQAw\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAIwAnoECAUQAw\">Clean your hands often. Use soap and water, or an alcohol-based hand rub.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQBA\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAMwAnoECAUQBA\">Maintain a safe distance from anyone who is coughing or sneezing.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQBQ\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAQwAnoECAUQBQ\">Wear a mask when physical distancing is not possible.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQBg\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAUwAnoECAUQBg\">Don’t touch your eyes, nose or mouth.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQBw\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAYwAnoECAUQBw\">Cover your nose and mouth with your bent elbow or a tissue when you cough or sneeze.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQCA\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAcwAnoECAUQCA\">Stay home if you feel unwell.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQCQ\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAgwAnoECAUQCQ\">If you have a fever, cough and difficulty breathing, seek medical attention.</div>\r\n</div>\r\n<div>\r\n<div class=\"CfnCCf\"> </div>\r\n</div>\r\n<div class=\"PZPZlf\"> </div>\r\n<div>\r\n<div class=\"CfnCCf\"> </div>\r\n</div>', 'https://www.telehealers.in/./assets/uploads/precautions/neighb.png', 1, '2021-04-30', 0),
(2, 'Clean your hands', '<div class=\"PZPZlf\">\r\n<div class=\"INGOOb\" data-hveid=\"CAUQAg\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAEwAnoECAUQAg\">To prevent the spread of COVID-19:</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQAw\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAIwAnoECAUQAw\">Clean your hands often. Use soap and water, or an alcohol-based hand rub.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQBA\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAMwAnoECAUQBA\">Maintain a safe distance from anyone who is coughing or sneezing.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQBQ\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAQwAnoECAUQBQ\">Wear a mask when physical distancing is not possible.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQBg\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAUwAnoECAUQBg\">Don’t touch your eyes, nose or mouth.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQBw\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAYwAnoECAUQBw\">Cover your nose and mouth with your bent elbow or a tissue when you cough or sneeze.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQCA\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAcwAnoECAUQCA\">Stay home if you feel unwell.</div>\r\n</div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"AupITe\" data-hveid=\"CAUQCQ\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAgwAnoECAUQCQ\">If you have a fever, cough and difficulty breathing, seek medical attention.</div>\r\n</div>\r\n<div>\r\n<div class=\"CfnCCf\"> </div>\r\n</div>\r\n<div class=\"PZPZlf\"> </div>\r\n<div class=\"PZPZlf\">\r\n<div class=\"INGOOb\" data-hveid=\"CAUQDA\" data-ved=\"2ahUKEwip-uK18KXwAhVq8HMBHZdrCsUQqsIBKAswAnoECAUQDA\"> </div>\r\n</div>', 'https://www.telehealers.in/./assets/uploads