INSERT INTO `symptoms_tbl` (`id`, `title`, `details`, `picture`, `post_by`, `post_date`, `post_category`) VALUES
(2, 'Most common symptoms:', '<ul>\r\n<li>Fever</li>\r\n<li>Dry cough</li>\r\n<li>Tiredness</li>\r\n</ul>', 'https://www.telehealers.in/./assets/uploads/blog/1.jpg', 1, '2021-04-30', 0),
(3, 'Less common symptoms:', '<ul>\r\n<li>Aches and pains</li>\r\n<li>Sore throat</li>\r\n<li>Diarrhoea</li>\r\n<li>conjunctivitis</li>\r\n<li>Headache</li>\r\n<li>Loss of taste or smell</li>\r\n<li>A rash on skin, or discolouration of fingers or toes</li>\r\n</ul>', 'https://www.telehealers.in/./assets/uploads/blog/11.jpg', 1, '2021-04-30', 0),
(4, 'Serious symptoms:', '<ul>\r\n<li>Difficulty breathing or shortness of breath</li>\r\n<li>Chest pain or pressure</li>\r\n<li>Loss of speech or movement</li>\r\n<li>conjunctivitis</li>\r\n</ul>\r\n<p>Seek immediate medical attention if you have serious symptoms. Always call before visiting your doctor or health facility.</p>\r\n<p>People with mild symptoms who are otherwise healthy should manage their symptoms at home.</p>\r\n<p>On average it takes 5-6 days from when someone is infected with the virus for symptoms to show, however it can take up to 14 days.</p>', 'https://www.telehealers.in/./assets/uploads/blog/12.jpg', 1, '2021-04-30', 0);
